  `view` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '浏览次数',
  `focus_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关注数',
  `runge` float(10,2) DEFAULT '0.00',
  `description` char(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL DEFAULT '' COMMENT '描述',
  `content` longtext CHARACTER SET utf8 COLLATE utf8_bin NOT NULL COMMENT '文章内容',
  PRIMARY KEY (`id`),
  KEY `category_id` (`category_id`),
  KEY `group_id` (`group_id`),
  KEY `status` (`status`)
