INSERT INTO `bb_pingxx` VALUES ('1', '支付宝 PC 网页支付', 'alipay_pc_direct', '/static/admin/img/pingxx/alipay.png', '1', 'alipay_pc_direct 适用于 PC 网页支付，需要开通支付宝即时到账服务。', '1', '1'), ('2', '微信扫码支付', 'wx_pub_qr', '/static/admin/img/pingxx/wx.png', '1', 'wx_pub_qr 是微信公众账号扫码支付，需要开通微信公众账号支付。', '2', '1'), ('3', '支付宝扫码支付', 'alipay_qr', '/static/admin/img/pingxx/alipay.png', '1', 'alipay_qr 是支付宝扫码支付，因为二维码的特殊性，所以其可以用于 PC 的支付场景。', '3', '1'), ('4', '银联网关支付', 'upacp_pc', '/static/admin/img/pingxx/upacp.png', '1', 'upacp_pc 适用于 PC 网页支付，需要开通银联网关支付服务', '4', '1'), ('5', '微信支付', 'wx_pub', '/static/admin/img/pingxx/wx.png', '2', 'wx_pub 是微信公众账号支付，只能用于微信内置浏览器内，而且只有服务号才能申请该支付功能。', '5', '1'), ('6', '支付宝手机支付', 'alipay_wap', '/static/admin/img/pingxx/alipay.png', '2', 'alipay_wap 适用于手机网页支付，需开通支付宝手机网页支付服务', '6', '1'), ('7', '银联手机支付', 'upacp_wap', '/static/admin/img/pingxx/upacp.png', '2', 'upacp_wap 适用于手机网页支付，限 2015 年元旦后的银联新商户使用，需要开通银联全渠道手机网页支付。', '7', '1'), ('8', '百度钱包手机支付', 'bfb_wap', '/static/admin/img/pingxx/bfb.png', '2', 'bfb_wap 适用于手机网页支付，需要开通百度钱包手机网页支付。', '8', '1'), ('9', '易宝一键支付', 'yeepay_wap', '/static/admin/img/pingxx/yeepay.png', '2', 'yeepay_wap 适用于移动端网页支付，需要与易宝当地分公司签署「易宝一键支付」服务协议。', '9', '0'), ('10', '京东支付', 'jdpay_wap', '/static/admin/img/pingxx/jdpay.png', '2', 'jdpay_wap 适用于手机网页支付，需开通京东支付移动版的服务。', '10', '0'), ('11', '支付宝支付', 'alipay', '/static/admin/img/pingxx/alipay.png', '3', 'alipay 适用于 App 支付，需要开通支付宝手机支付服务。', '11', '0'), ('12', '微信支付', 'wx', '/static/admin/img/pingxx/wx.png', '3', 'wx 适用于 App 支付，需要开通微信 App 支付服务。', '12', '0'), ('13', '百度钱包支付', 'bfb', '/static/admin/img/pingxx/bfb.png', '3', 'bfb 适用于 App 支付，需开通百度钱包移动快捷支付服务。', '13', '0'), ('14', ' Apple Pay', 'applepay_upacp', '/static/admin/img/pingxx/applepay.png', '3', 'applepay_upacp 即 Apple Pay ，适用于 App 支付，需要开通银联手机支付。', '15', '0'), ('15', '银联支付', 'upacp', '/static/admin/img/pingxx/upacp.png', '3', 'upacp 适用于 App 支付，限 2015 年元旦后的银联新商户使用。需要开通银联全渠道支付服务。', '14', '0');
COMMIT;

