INSERT INTO `bb_temp_bak` VALUES ('4', 'topic', 'index', 'index', '首页模版', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<link href=\"/static/assets/css/layout-question.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n\n<!-- SLIDER -->\n<section id=\"slider\" class=\"\" style=\"background:url(\'/static/assets/images/index.jpg\');height: 500px;background-position: center center\">\n	<span class=\"overlay dark-2\"><!-- dark overlay [0 to 9 opacity] --></span>\n\n	<div class=\"display-table\">\n		<div class=\"display-table-cell vertical-align-middle\">\n\n			<div class=\"container text-center\">\n\n				<h1 class=\"nomargin wow fadeInUp\" data-wow-delay=\"0.4s\">\n					WE ARE\n					<!--\n                        TEXT ROTATOR\n                        data-animation=\"fade|flip|flipCube|flipUp|spin\"\n                    -->\n					<span class=\"rotate \" data-animation=\"fade\" data-speed=\"1500\">\n									bb, MULTIPURPOSE, POWERFUL\n								</span>\n				</h1>\n\n				<p class=\"lead size-20 wow fadeInUp\" data-wow-delay=\"0.7s\">\n					一款基于ThinkJS的功能强大的（PC端,手机端和微信公众平台）电子商务平台及CMS建站系统\n					<!--Over <span class=\"countTo\" data-speed=\"4000\">550</span> html files!-->\n					<!--<span class=\"theme-color weight-400 font-style-italic\">Admin included</span> &amp; MLT-->\n				</p>\n\n				<a class=\"btn btn-default btn-lg\" href=\"/documentation\">Getting Started</a>\n			</div>\n\n		</div>\n	</div>\n\n</section>\n<!-- /SLIDER -->\n<!-- INFO BAR -->\n<section class=\"info-bar info-bar-clean \">\n<div class=\"container\">\n\n<div class=\"row\">\n\n<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n<i><img src=\"http://git.oschina.net/logo.gif\" style=\"width:32px; margin-top: -2px\"></i>\n<h3>码云主页</h3>\n<p>去 <a href=\"http://git.oschina.net/arterli/bb\" target=\"_blank\">码云</a> 查看最新的开发进度</p>\n</div>\n\n<div class=\"col-sm-3 padding-top-0 padding-bottom-0\">\n<i class=\"glyphicon glyphicon-download-alt\"></i>\n<h3>下载bb</h3>\n	{% t