\n                            <span class=\"font-lato\">{{val.category_id}}</span>\n                        </a>\n                        <a class=\"category\" href=\"#\">\n                            <span class=\"font-lato\">Photography</span>\n                        </a>\n                    </li>\n                    <li>\n                        <a href=\"#\">\n                            <i class=\"fa fa-user\"></i>\n                            <span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n                        </a>\n                    </li>\n                </ul>\n\n                <p>{{val.description}}</p>\n            </div>\n\n        </div>\n        <!-- /POST ITEM -->\n        {%endfor%}\n\n\n        <!-- PAGINATION -->\n        <div class=\"text-left\">\n            <!-- Pagination Default -->\n            {{pagination | safe}}\n            <!-- /Pagination Default -->\n        </div>\n        <!-- /PAGINATION -->\n\n    </div>\n\n<!-- RIGHT -->\n\n    <div class=\"col-md-3 col-sm-3\">\n\n        <!-- INLINE SEARCH -->\n        <div class=\"inline-search clearfix margin-bottom-30\">\n            <form action=\"\" method=\"get\" class=\"widget_search\">\n                <input type=\"search\" placeholder=\"Start Searching...\" id=\"s\" name=\"s\" class=\"serch-input\">\n                <button type=\"submit\">\n                    <i class=\"fa fa-search\"></i>\n                </button>\n            </form>\n        </div>\n        <!-- /INLINE SEARCH -->\n\n        <hr />\n\n        <!-- side navigation -->\n        {%column data=\"cate\",cid=category.id%}\n        {%if cate%}\n        <div class=\"side-nav margin-bottom-20 margin-top-20\">\n            <div class=\"side-nav-head\">\n                <button class=\"fa fa-bars\"></button>\n                <h4>{{category.title}} 子分类</h4>\n            </div>\n            <ul class=\"list-group list-group-bordered list-group-noicon uppercase\">\n                {%for val in cate%}\n                <li class=\"list-group-item\"><a href=\"{{val.url}}\"><span class=\"size-11 text-muted pull-right\">(12)</span> {{val.name}}</a></li>\n                {%endfor%}\n            </ul>\n            <!-- /side navigation -->\n        </div>\n        {%endif%}\n\n\n        <!-- JUSTIFIED TAB -->\n        <div class=\"tabs nomargin-top hidden-xs margin-bottom-10\">\n\n            <!-- tabs -->\n            <ul class=\"nav nav-tabs nav-bottom-border nav-justified\">\n                <li class=\"active\">\n                    <a href=\"#tab_1\" data-toggle=\"tab\">\n                        最热\n                    </a>\n                </li>\n                <li>\n                    <a href=\"#tab_2\" data-toggle=\"tab\">\n                        最新\n                    </a>\n                </li>\n            </ul>\n\n            <!-- tabs content -->\n            <div class=\"tab-content margin-bottom-0 margin-top-10\">\n\n                <!-- POPULAR -->\n                <div id=\"tab_1\" class=\"tab-pane active\">\n                    <!-- /post -->\n                    {% topic data = \"hotlist\",limit= \"5\",cid=category.id,type=\"hot\"%}\n                    {%for v in hotlist%}\n                    <div class=\"row tab-post\"><!-- post -->\n                        {%if v.cover_id%}\n                        <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                            <a href=\"{{v.name|get_url(v.id)}}\">\n                                <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                            </a>\n                        </div>\n                        {%endif%}\n                        <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                            <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                            <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                        </div>\n                    </div><!-- /post -->\n                    {%endfor%}\n\n                </div>\n                <!-- /POPULAR -->\n\n\n                <!-- RECENT -->\n                <div id=\"tab_2\" class=\"tab-pane\">\n                    {% topic data = \"newlist\",limit= \"5\",cid=category.id%}\n                    {%for v in newlist%}\n                    <div class=\"row tab-post\"><!-- post -->\n                        {%if v.cover_id%}\n                        <div class=\"col-md-3 col-sm-3 col-xs-3\">\n                            <a href=\"{{v.name|get_url(v.id)}}\">\n                                <img src=\"{{v.cover_id|get_pic(\'m=1,w=50,h=50\')}}\" width=\"50\" alt=\"\" />\n                            </a>\n                        </div>\n                        {%endif%}\n                        <div class=\"col-md-9 col-sm-9 col-xs-9\">\n                            <a href=\"{{v.name|get_url(v.id)}}\" class=\"tab-post-link\">{{v.title}}</a>\n                            <small>{{v.update_time|dateformat(\'Y-m-d H:i\')}}</small>\n                        </div>\n                    </div><!-- /post -->\n                    {%endfor%}\n                    <!-- /post -->\n                </div>\n                <!-- /RECENT -->\n\n            </div>\n\n        </div>\n        <!-- JUSTIFIED TAB -->\n\n\n        <!-- TAGS -->\n        <h3 class=\"hidden-xs size-16 margin-bottom-20\">TAGS</h3>\n        <div class=\"hidden-xs margin-bottom-20\">\n\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">RESPONSIVE</span>\n                <span class=\"num\">12</span>\n            </a>\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">CSS</span>\n                <span class=\"num\">3</span>\n            </a>\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">HTML</span>\n                <span class=\"num\">1</span>\n            </a>\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">JAVASCRIPT</span>\n                <span class=\"num\">28</span>\n            </a>\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">DESIGN</span>\n                <span class=\"num\">6</span>\n            </a>\n\n            <a class=\"tag\" href=\"#\">\n                <span class=\"txt\">DEVELOPMENT</span>\n                <span class=\"num\">3</span>\n            </a>\n        </div>\n\n\n\n        <hr />\n\n\n        <!-- SOCIAL ICONS -->\n        <div class=\"hidden-xs margin-top-30 margin-bottom-60\">\n            <a href=\"#\" class=\"social-icon social-icon-border social-facebook pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Facebook\">\n                <i class=\"icon-facebook\"></i>\n                <i class=\"icon-facebook\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-twitter pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Twitter\">\n                <i class=\"icon-twitter\"></i>\n                <i class=\"icon-twitter\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-gplus pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Google plus\">\n                <i class=\"icon-gplus\"></i>\n                <i class=\"icon-gplus\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-linkedin pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Linkedin\">\n                <i class=\"icon-linkedin\"></i>\n                <i class=\"icon-linkedin\"></i>\n            </a>\n\n            <a href=\"#\" class=\"social-icon social-icon-border social-rss pull-left\" data-toggle=\"tooltip\" data-placement=\"top\" title=\"Rss\">\n                <i class=\"icon-rss\"></i>\n                <i class=\"icon-rss\"></i>\n            </a>\n        </div>\n\n    </div>\n</div>\n\n</div>\n</section>\n<!-- / -->\n{% endblock %}', '1', '1', '7