INSERT INTO `bb_temp_bak` VALUES ('69', 'topic', 'index', 'index', '首页模版', '{% extends \"../inc/base.html\" %}\n{% block style %}\n<link href=\"/static/assets/css/layout-question.css\" rel=\"stylesheet\" type=\"text/css\" />\n{% endblock %}\n{% block content %}\n<section class=\"alternate parallax-2\">\n	<div class=\"overlay dark-0\"><!-- dark overlay [1 to 9 opacity] --></div>\n	<div class=\"container \" style=\"position: relative;z-index: 10\">\n\n		{% include \"../inc/banner.html\" %}\n		<div class=\"row\" >\n			<div class=\"col-md-12\">\n				<!-- REVOLUTION SLIDER -->\n				<div class=\"slider fullwidthbanner-container roundedcorners margin-bottom-0\">\n					<!--\n                        Navigation Styles:\n\n                            data-navigationStyle=\"\" theme default navigation\n\n                            data-navigationStyle=\"preview1\"\n                            data-navigationStyle=\"preview2\"\n                            data-navigationStyle=\"preview3\"\n                            data-navigationStyle=\"preview4\"\n\n                        Bottom Shadows\n                            data-shadow=\"1\"\n                            data-shadow=\"2\"\n                            data-shadow=\"3\"\n\n                        Slider Height (do not use on fullscreen mode)\n                            data-height=\"300\"\n                            data-height=\"350\"\n                            data-height=\"400\"\n                            data-height=\"450\"\n                            data-height=\"500\"\n                            data-height=\"550\"\n                            data-height=\"600\"\n                            data-height=\"650\"\n                            data-height=\"700\"\n                            data-height=\"750\"\n                            data-height=\"800\"\n                    -->\n					<!-- <div class=\"fullwidthbanner\" data-height=\"550\" data-shadow=\"\" data-navigationStyle=\"\">\n						<ul class=\"