n\n							<!-- IMAGE -->\n							{% if val.cover_id>0 %}\n							<figure class=\"blog-item-small-image margin-bottom-0\">\n								<img class=\"img-responsive\" src=\"{{val.cover_id|get_pic(\'m=1,w=200,h=120\')}}\" alt=\"\">\n							</figure>\n							{% endif %}\n							<div class=\"blog-item-small-content\">\n								<h2><a href=\"{{val.name|get_url(val.id)}}\" target=\"_blank\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n\n								<ul class=\"blog-post-info list-inline padding-bottom-10\">\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-clock-o\"></i>\n											<span class=\"font-lato\">{{val.update_time|dateformat(\'Y-m-d H:i\')}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-comment-o\"></i>\n											<span class=\"font-lato\">{{val.view}}</span>\n										</a>\n									</li>\n									<li>\n										<i class=\"fa fa-folder-open-o\"></i>\n\n										<a class=\"category\" href=\"{{categoryname.url}}\">\n											<span class=\"font-lato\">{{categoryname.name}}</span>\n										</a>\n									</li>\n									<li>\n										<a href=\"#\">\n											<i class=\"fa fa-user\"></i>\n											<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n										</a>\n									</li>\n								</ul>\n\n								<p>{{val.description|truncate(90, true, \"...\")}}</p>\n							</div>\n\n						</div>\n						<!-- /POST ITEM -->\n						{%endif%}\n						{%endfor%}\n					</div>\n					<div class=\"tab-pane fade\" id=\"wenda\">\n						<div class=\"timeline\">\n							<div class=\"timeline-hline\"><!-- horizontal line cache=\'1000\'--></div>\n							{%model data=\"list\",table=\"question\",order=\"update_time DESC\",limit=\"10\"%}\n							{% for val in list %}\n							{% set categoryname = val.category_id|get_cate %}\n							{% set imgs= val.detail|img_text_view(200,120)%}\n							<!-- POST ITEM -->\n							<div class=\"blog-post-item blog-post-item-inverse padding-top-20 padding-bottom-20 margin-bottom-0 clearfix\">\n								<div class=\"timeline-entry\" ><!-- .rounded = entry -->\n									<a href=\"#\"><img src=\"/uc/index/avatar/uid/{{val.uid}}\" style=\"width: 58px;height: 58px; margin-top: -10px\"></a>\n									<div class=\"timeline-vline\"><!-- vertical line --></div>\n								</div>\n								<!-- IMAGE -->\n								{% if imgs|length==1 %}\n								<figure class=\"blog-item-small-image margin-bottom-0\">\n									<img class=\"img-responsive\" src=\"{{imgs[0]}}\" alt=\"\">\n								</figure>\n								{% endif %}\n								<div class=\"blog-item-small-content\">\n									<h2><a href=\"/mod/question/{{val.id}}{{http.config(\'view.file_ext\')}}\" title=\"{{val.title}}\">{{val.title}}</a></h2>\n									<ul class=\"blog-post-info list-inline padding-bottom-10\">\n										<li>\n\n											<a class=\"\" href=\"{{categoryname.url}}\">\n												<span class=\"label label-light\">{{categoryname.name}}</span>\n											</a>\n										</li>\n										<li>{%if val.answer_users>0%}\n											<a href=\"#\">\n												<span class=\"font-lato\">{{val.answer_users|get_nickname}}</span>\n											</a>\n											<span class=\"font-lato\"> 回复了问题</span>\n											{%else%}\n											<a href=\"#\">\n												<span class=\"font-lato\">{{val.uid|get_nickname}}</span>\n											</a>\n											<span class=\"font-lato\"> 发起了问题</span>\n											{%endif%}\n										</li>\n										<li>\n                                <span class=\"font-lato\">\n                                {{val.focus_count}} 人关注\n                                    </span>\n										</li>\n										<li>\n											<span class=\"font-lato\">{{val.answer_count}} 个回复</span>\n\n										</li>\n										<li>\n											<span class=\"font-lato\">{{val.view}} 次浏览</span>\n										</li>\n										<li>\n											<span class=\"font-lato\"> {{val.update_time|moment}}</span>\n										</li>\n\n									</ul>\n									{% if imgs|length>1 %}\n									<div class=\"row margin-bottom-10\">\n										{%set i = 0%}\n										{%for img in imgs%}\n										{%if i< 4 %}\n										<div class=\"col-md-3\"><img class=\"img-responsive\" src=\"{{img}}\" alt=\"\"></div>\n										{%endif%}\n										{%set i=i+1%}\n										{%endfor%}\n									</div>\n									{%endif%}\n									<p>{{val.detail|delhtmltags|truncate(90, true, \"...\")|safe}}</p>\n								</div>\n\n							</div>\n							<!-- /POST ITEM -->\n							{%endfor%}\n\n						</div>\n\n						<!-- PAGINATION -->\n\n						<a class=\"btn btn-featured btn-default btn-inverse\" href=\"/question.html\" >\n							<span>今天，你遇到了什么问题呢？进入社区寻求答案！</span>\n							<i class=\"et-happy\"></i>\n						</a>\n						<!-- /PAGINATION -->\n					</div>\n\n\n				</div>\n			</div>\n			<!-- LEFT -->\n			<div class=\"col-md-3 col-sm-3\">\n\n				{% if controller.is_login %}\n				<div class=\"alert alert-info margin-bottom-20 box-shadow-4\"><!-- INFO -->\n					今天，你遇到了什么问题呢？\n					<a href=\"/mod/question/sys/add/cid/123\" class=\"btn btn-primary btn-3d btn-block margin-top-10\">提问</a>\n				</div>\n				{%else%}\n				<div class=\"alert alert-info margin-bottom-20 box-shadow-4\"><!-- INFO -->\n					你好! 你还没有登录，请先登录.\n					<a class=\"btn btn-block btn-social btn-dropbox margin-top-10  \" href=\"/uc/public/login\" data-toggle=\"ajaxModal\">\n						<i class=\"fa fa-sign-in\"></i> 登录{{controller.setup.WEB_SITE_TITLE}}\n					</a>\n				</div>\n				{%endif%}\n\n				<div class=\"box-inner nopadding  margin-bottom-20\">\n					<h3>\n						<a class=\"pull-right size-11 text-warning\" href=\"/topic\">更多</a>\n						热门话题\n					</h3>\n					<div class=\"\" >\n						{% keywords data =\"topic\",type=\"hot\",limit=\"5\" %}\n						{%for k in topic%}\n						<div class=\"clearfix margin-bottom-10\"><!-- discussion item -->\n							{%if k.pic==0%}\n							<i class=\"ico-light ico-60 ico-hover et-chat pull-left margin-bottom-0 margin-top-0\" ></i>\n							{%else%}\n							<img class=\"thumbnail pull-left\" src=\"/static/assets/images/demo/people/300x300/4-min.jpg\" width=\"60\" height=\"60\" alt=\"\" />\n							{%endif%}\n							<h4 class=\" nomargin noborder nopadding bold\"><a class=\"tag \" href=\"{{k.url}}\">\n								<span class=\"txt\">{{k.keyname}}</span>\n								<span class=\"num\"><i class=\"fa fa-fire\"></i></span>\n							</a>\n							</h4>\n							<span class=\"size-13 text-muted\">\n                                {{k.videonum}} 个主题, {{k.focus_count}} 人关注\n												</span>\n						</div><!-- /discussion item -->\n						{%endfor%}\n\n\n					</div>\n				</div>\n\n\n				{%model data=\"data\",table=\"question_user\",cache=\'1000\',order=\"question_count DESC\",alias=\"a\",limit=\"10\",join=\'{\"member\": {\"join\": \"left\",\"as\":\"b\",\"on\": [\"a.uid\", \"b.id\"]}}\'%}\n				{%if data.length>0%}\n				<div class=\"box-inner nopadding  margin-bottom-20\">\n					<h3>\n						<a class=\"pull-right size-11 text-warning\" href=\"#\">更多</a>\n						热门用户\n					</h3>\n					<div class=\"\" >\n						{%for user in data%}\n						<div class=\"clearfix margin-bottom-10\"><!-- squared item -->\n							<img class=\"thumbnail pull-left rounded\" src=\"/uc/index/avatar/uid/{{user.uid}}\" width=\"40\" height=\"40\" alt=\"\" />\n							<h4 class=\"size-14 nomargin noborder nopadding bold\"><a href=\"#\">{{user.username}}</a></h4>\n							<span class=\"size-12 text-muted\">{{user.question_count}} 个问题, {{user.agree_count}} 次赞同</span>\n						</div><!-- /squared item -->\n						{%endfor%}\n\n					</div>\n				</div>\n\n				{%endif%}\n\n				\n			</div>\n\n\n		</div>\n\n\n	</div>\n</section>\n\n<!--\ncontrolls-over		= navigation buttons over the image\nbuttons-autohide 	= navigation buttons visible on mouse hover only\n\ndata-plugin-options:\n\"singleItem\": true\n\"autoPlay\": true (or ms. eg: 4000)\n\"navigation\": true\n\"pagination\": true\n-->\n<!-- <section class=\"heading-title \" style=\"padding-top: 10px ;padding-bottom: 10px;background-color: rgba(0,0,0,0.05)\">\n	<div class=\"container\">\n\n		\n		<div class=\"heading-title heading-border heading-color nomargin\">\n			<h4 style=\"background-color: rgba(0,0,0,0);color: #333\">友情链接 <span>Friendship link</span></h4>\n			<p class=\"font-lato size-14\"><a href=\"/ext/link/index/apply\">申请友情链接</a> | <a href=\"/ext/link/index\">更多&gt;&gt;</a> </p>\n		</div>\n\n	</div>\n</section> -->\n<!-- <div class=\"text-center margin-top-20 margin-bottom-10 container link\">\n	<div class=\"owl-carousel nomargin\" data-plugin-options=\'{\"items\":6, \"singleItem\": false, \"autoPlay\": true}\'>\n		{% model data =\"link\",table=\"ext_link\",where=\'{\"passed\":1}\',limit=\"24\",order=\"sort ASC\"%}\n		{%for l in link%}\n		{%if l.linktype==0%}\n		<div class=\"linkbox alternate\">\n			<a href=\"{{l.url}}\" target=\"_blank\" title=\"{{l.name}}\">{{l.name}}</a>\n		</div>\n		{%else%}\n		<div class=\"linkbox alternate\">\n			<a href=\"{{l.url}}\" target=\"_blank\" title=\"{{l.name}}\">\n				<img class=\"img-responsive\" src=\"{{l.logo}}\" alt=\"{{l.name}}\">\n			</a>\n		</div>\n		{%endif%}\n		{%endfor%}\n	</div>\n</div> -->\n{% endblock %}\n{%block script%}\n\n{%endblock%}', '1', '1', '1', '1498473322989', '1');
