SELECT ROUND(RAND()*100,0)+1 INTO v_cnt;
START TRANSACTION;
UPDATE bb_order_seq SET order_sn=order_sn+v_cnt WHERE timestr = v_timestr;
IF ROW_COUNT()=0 THEN
INSERT INTO bb_order_seq(timestr,order_sn) VALUES(v_timestr,v_cnt);
END IF;
SELECT CONCAT(v_timestr,LPAD(order_sn,7,0)) AS order_sn FROM bb_order_seq WHERE timestr=v_timestr;
COMMIT;
END
